task wb_read_strobe;

	input [31:0] wb_address_test;				
	input		 check_for_truth;
	input [31:0] truth_data_test;

	begin
		@(posedge clk) begin
			i_wb_adr <= wb_address_test;	
			i_wb_cyc <= 1'b1;			
			i_wb_stb <= 1'b1;			
			i_wb_we  <= 1'b0;			
		end

		@(posedge clk);

		@(posedge clk) begin
			i_wb_cyc <= 1'b0;
			i_wb_stb <= 1'b0;
		end

		if (check_for_truth) begin
			if (o_wb_dat !== truth_data_test) begin
				$display("%t ERROR: failure found when truth_data = %h. Is %h but should be %h",
			  	$time, truth_data_test, o_wb_dat, truth_data_test); 
				`ifdef ERRORSTOP
					$stop;
				`endif
			end
		end
	end
endtask

task wb_write_strobe;

	input [31:0] wb_waddress_test;	
	input [127:0] wb_data_test;		

	begin
		@(posedge clk) begin			
			i_wb_adr <= wb_waddress_test;	
			i_wb_cyc <= 1'b1;			
			i_wb_stb <= 1'b1;			
			i_wb_we  <= 1'b1;			
			i_wb_dat <= wb_data_test;	
		end


		@(posedge clk) begin
			i_wb_we  <= 1'b0;			
			i_wb_cyc <= 1'b0;			
			i_wb_stb <= 1'b0;			
		end
	end
endtask

initial begin
	$timeformat(-9,2,"ns", 16);
	`ifdef SDFSCAN
		$sdf_annotate("sdf/boot_mem128_tsmc18_scan.sdf", test.top);
	`endif
	clk <= 1'b0;
	reset <= 1'b0;
	scan_in0 <= 1'b0;
	scan_in1 <= 1'b0;
	scan_in2 <= 1'b0;
	scan_in3 <= 1'b0;
	scan_in4 <= 1'b0;
	scan_enable <= 1'b0;
	test_mode <= 1'b0;

	i_wb_adr <= 'd0;
	i_wb_sel <= 16'h0000;
	i_wb_we  <= 1'b0;
	i_wb_dat <= 'd0;
	i_wb_cyc <= 1'b0;
	i_wb_stb <= 1'b0;

	@(posedge clk) begin
		reset <= 1'b1;
	end

	@(posedge clk);

	@(negedge clk) begin
		reset <= 1'b0;
	end

	for (i=0; i<1024; i=i+1) begin
		i_wb_sel <= 16'hFFFF;
		wb_write_strobe({18'h00000, i[9:0], 4'h0}, i);
	end
	for (i=0; i<1024; i=i+1) begin
		i_wb_sel <= 16'hFFFF;
		wb_read_strobe({18'h00000, i[9:0], 4'h0}, 0, 0);
	end

    $finish;
end
