always @( posedge i_clk )
    if (control_state == EXECUTE && ((instruction[0] === 1'bx) || (instruction[31] === 1'bx)))
        begin
        `TB_ERROR_MESSAGE
        $display("Instruction with x's =%08h", instruction);
        end

always @ ( posedge i_clk )
    if ( !i_core_stall )
        begin
        irq   <= i_irq;
        firq  <= i_firq;

        if ( control_state == INT_WAIT1 && status_bits_mode_r == SVC )
            begin
            dabt_reg  <= 1'd0;
            end
        else
            begin
            dabt_reg  <= dabt_reg || i_dabt;
            end

        dabt_reg_d1  <= dabt_reg;
        end

always @ ( posedge i_clk )
    if ( !i_core_stall )
        begin





        if      ( type == MTRANS )
            begin
            saved_current_instruction              <= mtrans_instruction_nxt;
            saved_current_instruction_type         <= type;
            saved_current_instruction_iabt         <= instruction_iabt;
            saved_current_instruction_adex         <= instruction_adex;
            saved_current_instruction_address      <= instruction_address;
            saved_current_instruction_iabt_status  <= instruction_iabt_status;
            end
        else if ( saved_current_instruction_wen )
            begin
            saved_current_instruction              <= instruction;
            saved_current_instruction_type         <= type;
            saved_current_instruction_iabt         <= instruction_iabt;
            saved_current_instruction_adex         <= instruction_adex;
            saved_current_instruction_address      <= instruction_address;
            saved_current_instruction_iabt_status  <= instruction_iabt_status;
            end

        if      ( pre_fetch_instruction_wen )
            begin
            pre_fetch_instruction                  <= fetch_instruction_r;
            pre_fetch_instruction_type             <= fetch_instruction_type_r;
            pre_fetch_instruction_iabt             <= iabt_reg;
            pre_fetch_instruction_adex             <= adex_reg;
            pre_fetch_instruction_address          <= fetch_address_r;
            pre_fetch_instruction_iabt_status      <= abt_status_reg;
            end



        hold_instruction              <= instruction;
        hold_instruction_type         <= type;
        hold_instruction_iabt         <= instruction_iabt;
        hold_instruction_adex         <= instruction_adex;
        hold_instruction_address      <= instruction_address;
        hold_instruction_iabt_status  <= instruction_iabt_status;
        end

always @ ( posedge i_clk )
    if ( !i_core_stall )
        begin
        if (!conflict)
            begin
            fetch_instruction_r         <= i_fetch_instruction;
            fetch_instruction_type_r    <= instruction_type(i_fetch_instruction);
            fetch_address_r             <= i_execute_iaddress;
            iabt_reg                    <= i_iabt;
            adex_reg                    <= i_adex;
            abt_status_reg              <= i_abt_status;
            end

        status_bits_mode_r          <= status_bits_mode_nxt;
        status_bits_irq_mask_r      <= status_bits_irq_mask_nxt;
        status_bits_firq_mask_r     <= status_bits_firq_mask_nxt;
        o_imm32                     <= imm32_nxt;
        o_imm_shift_amount          <= imm_shift_amount_nxt;
        o_shift_imm_zero            <= shift_imm_zero_nxt;






        condition_r                 <= instruction_valid && !interrupt ? condition_nxt : AL;
        o_decode_exclusive          <= decode_exclusive_nxt;
        decode_iaccess_r            <= decode_iaccess_nxt;
        o_decode_daccess            <= decode_daccess_nxt;

        o_rm_sel                    <= rm_sel_nxt;
        o_rs_sel                    <= rs_sel_nxt;
        o_load_rd                   <= load_rd_nxt;
        load_rd_d1                  <= load_rd_d1_nxt;
        load_pc_r                   <= load_pc_nxt;
        o_rn_sel                    <= rn_sel_nxt;
        o_barrel_shift_amount_sel   <= barrel_shift_amount_sel_nxt;
        o_barrel_shift_data_sel     <= barrel_shift_data_sel_nxt;
        o_barrel_shift_function     <= barrel_shift_function_nxt;
        o_alu_function              <= alu_function_nxt;
        o_use_carry_in              <= use_carry_in_nxt;
        o_multiply_function         <= multiply_function_nxt;
        o_interrupt_vector_sel      <= next_interrupt;
        iaddress_sel_r              <= iaddress_sel_nxt;
        daddress_sel_r              <= daddress_sel_nxt;
        pc_sel_r                    <= pc_sel_nxt;
        o_byte_enable_sel           <= byte_enable_sel_nxt;
        o_status_bits_sel           <= status_bits_sel_nxt;
        o_reg_write_sel             <= reg_write_sel_nxt;
        o_firq_not_user_mode        <= firq_not_user_mode_nxt;
        o_write_data_wen            <= write_data_wen_nxt;
        o_base_address_wen          <= base_address_wen_nxt;
        pc_wen_r                    <= pc_wen_nxt;
        o_reg_bank_wen              <= reg_bank_wen_nxt;
        o_status_bits_flags_wen     <= status_bits_flags_wen_nxt;
        o_status_bits_mode_wen      <= status_bits_mode_wen_nxt;
        o_status_bits_irq_mask_wen  <= status_bits_irq_mask_wen_nxt;
        o_status_bits_firq_mask_wen <= status_bits_firq_mask_wen_nxt;

        o_copro_opcode1             <= instruction[23:21];
        o_copro_opcode2             <= instruction[7:5];
        o_copro_crn                 <= instruction[19:16];
        o_copro_crm                 <= instruction[3:0];
        o_copro_num                 <= instruction[11:8];
        o_copro_operation           <= copro_operation_nxt;
        o_copro_write_data_wen      <= copro_write_data_wen_nxt;
        restore_base_address        <= restore_base_address_nxt;
        control_state               <= control_state_nxt;
        end

always @*
   begin

   control_state_nxt = control_state;


   if ( control_state == RST_WAIT1 )     control_state_nxt = RST_WAIT2;
   if ( control_state == RST_WAIT2 )     control_state_nxt = EXECUTE;
   if ( control_state == INT_WAIT1 )     control_state_nxt = INT_WAIT2;
   if ( control_state == INT_WAIT2 )     control_state_nxt = EXECUTE;
   if ( control_state == COPRO_WAIT )    control_state_nxt = PRE_FETCH_EXEC;
   if ( control_state == PC_STALL1 )     control_state_nxt = PC_STALL2;
   if ( control_state == PC_STALL2 )     control_state_nxt = EXECUTE;
   if ( control_state == SWAP_WRITE )    control_state_nxt = SWAP_WAIT1;
   if ( control_state == SWAP_WAIT1 )    control_state_nxt = SWAP_WAIT2;
   if ( control_state == MULT_STORE )    control_state_nxt = PRE_FETCH_EXEC;
   if ( control_state == MTRANS_ABORT )  control_state_nxt = PRE_FETCH_EXEC;

   if ( control_state == MEM_WAIT1 )
       control_state_nxt = MEM_WAIT2;

   if ( control_state == MEM_WAIT2   ||
       control_state == SWAP_WAIT2    )
       begin
       if ( write_pc ) 
           control_state_nxt = PC_STALL1;
       else
           control_state_nxt = PRE_FETCH_EXEC;
       end

   if ( control_state == MTRANS_EXEC1 )
       begin
       if ( mtrans_instruction_nxt[15:0] != 16'd0 )
           control_state_nxt = MTRANS_EXEC2;
       else   
           begin
           if ( dabt ) 
               control_state_nxt = MTRANS_ABORT;
           else if ( write_pc ) 
               control_state_nxt = MEM_WAIT1;
           else
               control_state_nxt = PRE_FETCH_EXEC;
           end
       end



   if ( control_state == MTRANS_EXEC2 && mtrans_num_registers == 5'd1 )
       begin
       if ( dabt ) 
           control_state_nxt = MTRANS_ABORT;
       else if ( write_pc ) 
           control_state_nxt = MEM_WAIT1;
       else
           control_state_nxt = PRE_FETCH_EXEC;
       end


   if ( control_state == MULT_PROC1 )
       begin
       if (!instruction_execute)
           control_state_nxt = PRE_FETCH_EXEC;
       else
           control_state_nxt = MULT_PROC2;
       end

   if ( control_state == MULT_PROC2 )
       begin
       if ( i_multiply_done )
           if      ( o_multiply_function[1] )  
               control_state_nxt = MULT_ACCUMU;
           else
               control_state_nxt = MULT_STORE;
       end


   if ( control_state == MULT_ACCUMU )
       begin
       control_state_nxt = MULT_STORE;
       end




   if ( instruction_valid )
       begin


       control_state_nxt = EXECUTE;

       if ( current_write_pc )
            control_state_nxt = PC_STALL1;

       if ( load_op && instruction[15:12]  == 4'd15 )  
            control_state_nxt = MEM_WAIT1;


       if ( type == MTRANS && instruction[20] && mtrans_reg1 == 4'd15 ) 
            control_state_nxt = MEM_WAIT1;

       if ( type == MTRANS && !conflict && mtrans_num_registers != 5'd0 && mtrans_num_registers != 5'd1 )
           control_state_nxt = MTRANS_EXEC1;

       if ( type == MULT && !conflict )
               control_state_nxt = MULT_PROC1;

       if ( type == SWAP && !conflict )
               control_state_nxt = SWAP_WRITE;

       if ( type == CORTRANS && !und_request && !conflict )
               control_state_nxt = COPRO_WAIT;


       if ( interrupt && !conflict )
               control_state_nxt = INT_WAIT1;
       end

   end

always @*
    begin

    status_bits_mode_nxt            = i_execute_status_bits[1:0];   

    status_bits_irq_mask_nxt        = status_bits_irq_mask_r;
    status_bits_firq_mask_nxt       = status_bits_firq_mask_r;
    decode_exclusive_nxt            = 1'd0;
    decode_daccess_nxt              = 1'd0;
    decode_iaccess_nxt              = 1'd1;
    copro_operation_nxt             = 'd0;


    saved_current_instruction_wen   = 1'd0;
    pre_fetch_instruction_wen       = 1'd0;
    restore_base_address_nxt        = restore_base_address;


    barrel_shift_amount_sel_nxt     = 'd0;  
    barrel_shift_data_sel_nxt       = 'd0;  
    barrel_shift_function_nxt       = 'd0;
    use_carry_in_nxt                = 'd0;
    multiply_function_nxt           = 'd0;
    iaddress_sel_nxt                = 'd0;
    daddress_sel_nxt                = 'd0;
    pc_sel_nxt                      = 'd0;
    load_pc_nxt                     = 'd0;
    byte_enable_sel_nxt             = 'd0;
    status_bits_sel_nxt             = 'd0;
    reg_write_sel_nxt               = 'd0;
    o_user_mode_regs_store_nxt      = 'd0;


    alu_swap_sel_nxt                = 'd0;
    alu_not_sel_nxt                 = 'd0;
    alu_cin_sel_nxt                 = 'd0;
    alu_cout_sel_nxt                = 'd0;
    alu_out_sel_nxt                 = 'd0;


    write_data_wen_nxt              = 'd0;
    copro_write_data_wen_nxt        = 'd0;
    base_address_wen_nxt            = 'd0;
    pc_wen_nxt                      = 'd1;
    reg_bank_wen_nxt                = 'd0;  

    status_bits_flags_wen_nxt       = 'd0;
    status_bits_mode_wen_nxt        = 'd0;
    status_bits_irq_mask_wen_nxt    = 'd0;
    status_bits_firq_mask_wen_nxt   = 'd0;

    if ( instruction_valid && !interrupt && !conflict )
        begin
        if ( type == REGOP )
            begin
            if ( !opcode_compare )
                begin

                if (instruction[15:12]  == 4'd15)
                    begin
                    pc_sel_nxt       = 3'd1; 
                    iaddress_sel_nxt = 4'd1; 
                    end
                else
                    reg_bank_wen_nxt = decode (instruction[15:12]);
                end

            if ( !immediate_shift_op )
                begin
                barrel_shift_function_nxt  = instruction[6:5];
                end

            if ( !immediate_shift_op )
                barrel_shift_data_sel_nxt = 2'd2; 

            if ( !immediate_shift_op && instruction[4] )
                barrel_shift_amount_sel_nxt = 2'd1; 

            if ( !immediate_shift_op && !instruction[4] )
                barrel_shift_amount_sel_nxt = 2'd2; 


            if ( opcode == AND || opcode == EOR || opcode == TST || opcode == TEQ ||
                 opcode == ORR || opcode == MOV || opcode == BIC || opcode == MVN )
                status_bits_sel_nxt = 3'd5;

            if ( opcode == ADD || opcode == CMN )   
                begin
                alu_out_sel_nxt  = 4'd1; 
                use_carry_in_nxt = shift_extend;
                end

            if ( opcode == ADC ) 
                begin
                alu_out_sel_nxt  = 4'd1; 
                alu_cin_sel_nxt  = 2'd2; 
                use_carry_in_nxt = shift_extend;
                end

            if ( opcode == SUB || opcode == CMP ) 
                begin
                alu_out_sel_nxt  = 4'd1; 
                alu_cin_sel_nxt  = 2'd1; 
                alu_not_sel_nxt  = 1'd1; 
                end





            if ( opcode == SBC ) 
                begin
                alu_out_sel_nxt  = 4'd1; 
                alu_cin_sel_nxt  = 2'd2; 
                alu_not_sel_nxt  = 1'd1; 
                use_carry_in_nxt = 1'd1;
                end

            if ( opcode == RSB ) 
                begin
                alu_out_sel_nxt  = 4'd1; 
                alu_cin_sel_nxt  = 2'd1; 
                alu_not_sel_nxt  = 1'd1; 
                alu_swap_sel_nxt = 1'd1; 
                use_carry_in_nxt = 1'd1;
                end

            if ( opcode == RSC ) 
                begin
                alu_out_sel_nxt  = 4'd1; 
                alu_cin_sel_nxt  = 2'd2; 
                alu_not_sel_nxt  = 1'd1; 
                alu_swap_sel_nxt = 1'd1; 
                use_carry_in_nxt = 1'd1;
                end

            if ( opcode == AND || opcode == TST ) 
                begin
                alu_out_sel_nxt  = 4'd8;  
                alu_cout_sel_nxt = 1'd1;  
                end

            if ( opcode == EOR || opcode == TEQ ) 
                begin
                alu_out_sel_nxt = 4'd6;  
                alu_cout_sel_nxt = 1'd1; 
                use_carry_in_nxt = 1'd1;
                end

            if ( opcode == ORR )
                begin
                alu_out_sel_nxt  = 4'd7; 
                alu_cout_sel_nxt = 1'd1;  
                use_carry_in_nxt = 1'd1;
                end

            if ( opcode == BIC ) 
                begin
                alu_out_sel_nxt  = 4'd8;  
                alu_not_sel_nxt  = 1'd1;  
                alu_cout_sel_nxt = 1'd1;  
                use_carry_in_nxt = 1'd1;
               end

            if ( opcode == MOV ) 
                begin
                alu_cout_sel_nxt = 1'd1;  
                use_carry_in_nxt = 1'd1;
                end

            if ( opcode == MVN ) 
                begin
                alu_not_sel_nxt  = 1'd1; 
                alu_cout_sel_nxt = 1'd1;  
                use_carry_in_nxt = 1'd1;
               end
            end


        if ( mem_op )
            begin
            if ( load_op && instruction[15:12]  == 4'd15 ) 
                begin
                saved_current_instruction_wen   = 1'd1; 
                pc_wen_nxt                      = 1'd0; 
                load_pc_nxt                     = 1'd1;
                end

            decode_daccess_nxt              = 1'd1; 
            alu_out_sel_nxt                 = 4'd1; 

            if ( !instruction[23] )  
                begin
                alu_cin_sel_nxt  = 2'd1; 
                alu_not_sel_nxt  = 1'd1; 
                end

            if ( store_op )
                begin
                write_data_wen_nxt = 1'd1;
                if ( type == TRANS && instruction[22] )
                    byte_enable_sel_nxt = 2'd1;         
                end



            if ( mem_op_pre_indexed || mem_op_post_indexed )
                begin

                if ( rn_sel_nxt  == 4'd15 )
                    pc_sel_nxt = 3'd1;
                else
                    reg_bank_wen_nxt = decode ( rn_sel_nxt );
                end


            if ( mem_op_post_indexed )
               daddress_sel_nxt = 4'd4; 
            else
               daddress_sel_nxt = 4'd1; 

            if ( instruction[25] && type ==  TRANS )
                barrel_shift_data_sel_nxt = 2'd2; 

            if ( type == TRANS && instruction[25] && shift_imm != 5'd0 )
                begin
                barrel_shift_function_nxt   = instruction[6:5];
                barrel_shift_amount_sel_nxt = 2'd2; 
                end
            end


        if ( type == BRANCH )
            begin
            pc_sel_nxt            = 3'd1; 
            iaddress_sel_nxt      = 4'd1; 
            alu_out_sel_nxt       = 4'd1; 

            if ( instruction[24] ) 
                begin
                reg_bank_wen_nxt  = decode (4'd14);  
                reg_write_sel_nxt = 3'd1;            
                end
            end


        if ( type == MTRANS )
            begin
            saved_current_instruction_wen   = 1'd1; 
            decode_daccess_nxt              = 1'd1; 
            alu_out_sel_nxt                 = 4'd1; 
            base_address_wen_nxt            = 1'd1; 


            if ( mtrans_num_registers > 4'd1 )
                begin
                iaddress_sel_nxt        = 4'd3; 
                pc_wen_nxt              = 1'd0; 
                end






            restore_base_address_nxt        = instruction[20] &&
                                                (instruction[15:0] & (1'd1 << instruction[19:16]));


            if ( instruction[23] )
                begin
                if ( instruction[24] )    
                    daddress_sel_nxt = 4'd7; 
                else
                    daddress_sel_nxt = 4'd4; 
                end
            else

                begin
                alu_cin_sel_nxt  = 2'd1; 
                alu_not_sel_nxt  = 1'd1; 
                if ( !instruction[24] )    
                    daddress_sel_nxt  = 4'd6; 
                else
                    daddress_sel_nxt  = 4'd1; 
                end


            if ( !instruction[20] )  
                write_data_wen_nxt = 1'd1;


            if ( {instruction[22],instruction[20]} == 2'b10 )
                o_user_mode_regs_store_nxt = 1'd1;


            if ( instruction[21] )  
                reg_bank_wen_nxt  = decode (rn_sel_nxt);


            if ( instruction[20] && mtrans_reg1 == 4'd15 ) 
                begin
                saved_current_instruction_wen   = 1'd1; 
                pc_wen_nxt                      = 1'd0; 
                load_pc_nxt                     = 1'd1;
                end
            end


        if ( type == MULT )
            begin
            multiply_function_nxt[0]        = 1'd1; 

            saved_current_instruction_wen   = 1'd1; 

            pc_wen_nxt                      = 1'd0; 

            if ( instruction[21] )
                multiply_function_nxt[1]    = 1'd1; 
            end



        if ( type == SWAP )
            begin
            saved_current_instruction_wen   = 1'd1; 
            pc_wen_nxt                      = 1'd0; 
            decode_iaccess_nxt              = 1'd0; 
            decode_daccess_nxt              = 1'd1; 
            barrel_shift_data_sel_nxt       = 2'd2; 
            daddress_sel_nxt                = 4'd4; 
            decode_exclusive_nxt            = 1'd1; 
            end



        if ( type == CORTRANS && !und_request )
            begin
            saved_current_instruction_wen   = 1'd1; 
            pc_wen_nxt                      = 1'd0; 
            iaddress_sel_nxt                = 4'd3; 

            if ( instruction[20] ) 
                copro_operation_nxt         = 2'd1;  
            else 
                begin


                copro_operation_nxt      = 2'd0;
                copro_write_data_wen_nxt = 1'd1;  
                end
            end


        if ( type == SWI || und_request )
            begin

            reg_write_sel_nxt               = 3'd1;            
            reg_bank_wen_nxt                = decode (4'd14);  

            iaddress_sel_nxt                = 4'd2;            
            pc_sel_nxt                      = 3'd2;            

            status_bits_mode_nxt            = interrupt_mode;  
            status_bits_mode_wen_nxt        = 1'd1;


            status_bits_irq_mask_nxt        = 1'd1;
            status_bits_irq_mask_wen_nxt    = 1'd1;
            end


        if ( regop_set_flags )
            begin
            status_bits_flags_wen_nxt = 1'd1;



            if ( instruction[15:12] == 4'd15 )
                begin
                status_bits_sel_nxt       = 3'd1; 


                if ( i_execute_status_bits[1:0] != USR )
                    begin
                    status_bits_mode_wen_nxt      = 1'd1;
                    status_bits_irq_mask_wen_nxt  = 1'd1;
                    status_bits_firq_mask_wen_nxt = 1'd1;
                    end
                end
            end

        end

assign und_request  =   type == CODTRANS ||
                        type == COREGOP  ||
                      ( type == CORTRANS && instruction[11:8] != 4'd15 );

assign firq_request = firq && !i_execute_status_bits[26];
assign irq_request  = irq  && !i_execute_status_bits[27];
assign swi_request  = type == SWI;
assign dabt_request = dabt_reg;

always @*
    casez (instruction[15:0])
    16'b???????????????1 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 1],  1'd0};
    16'b??????????????10 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 2],  2'd0};
    16'b?????????????100 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 3],  3'd0};
    16'b????????????1000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 4],  4'd0};
    16'b???????????10000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 5],  5'd0};
    16'b??????????100000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 6],  6'd0};
    16'b?????????1000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 7],  7'd0};
    16'b????????10000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 8],  8'd0};
    16'b???????100000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15: 9],  9'd0};
    16'b??????1000000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15:10], 10'd0};
    16'b?????10000000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15:11], 11'd0};
    16'b????100000000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15:12], 12'd0};
    16'b???1000000000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15:13], 13'd0};
    16'b??10000000000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15:14], 14'd0};
    16'b?100000000000000 : mtrans_instruction_nxt = {instruction[31:16], instruction[15   ], 15'd0};
    default              : mtrans_instruction_nxt = {instruction[31:16],                     16'd0};
    endcase

always @( posedge i_clk )
    if ( !i_core_stall )
        begin
        conflict_r              <= conflict;
        instruction_execute_r   <= instruction_execute;
        rn_conflict1_r          <= rn_conflict1 && instruction_execute;
        rm_conflict1_r          <= rm_conflict1 && instruction_execute;
        rs_conflict1_r          <= rs_conflict1 && instruction_execute;
        rd_conflict1_r          <= rd_conflict1 && instruction_execute;
        o_rn_use_read           <= instruction_valid && ( rn_conflict1_r || rn_conflict2 );
        o_rm_use_read           <= instruction_valid && ( rm_conflict1_r || rm_conflict2 );
        o_rs_use_read           <= instruction_valid && ( rs_conflict1_r || rs_conflict2 );
        o_rd_use_read           <= instruction_valid && ( rd_conflict1_r || rd_conflict2 );
        end
