blocks:
always @ (posedge clk) begin
	if (rst) begin
		s_count <= 0;
		s_clk <= 0;
	end
	else begin 
		if (s_count < s_max) begin
			s_count <= s_count + 1;
		end
		else begin
			s_count <= 0;
			s_clk <= ~s_clk;
		end
	end
end

always @ (s_clk) begin
	if (rst) begin
		LR_count <= 0;
		LR_clk <= 0;
	end
	else if (s_clk == 0) begin      


		if (LR_count < (DIN_W-1) * LR_SAM) begin
			LR_count <= LR_count + 1;
		end
		else begin
			LR_count <= 0;
			LR_clk <= ~LR_clk;
		end
	end
	else
		begin
		LR_count <= LR_count;
		LR_clk <= LR_clk;
		end
end

always @ (posedge clk) begin
	if (rst) m_clk <= 0;
	else m_clk <= ~m_clk;
end

always @ (negedge s_clk) begin
	case (PS) 
		IDLE:   begin
			DONE <= 0;
			shift_count_0 <= 0;
			shift_count_1 <= 0;
			data_out <= 0;
		end
		CHAN_0:  begin

			if (shift_count_0 < DIN_W-1) begin
				shift_count_0 <= shift_count_0 + 1;
				data_out <= data_in[shift_count_0];
			end
			else begin
				shift_count_0 <= 0;
				data_out <= data_in[shift_count_0];
			end

			if (shift_count_0 == 15) DONE <= 1;
			else DONE <= 0;
		end

		CHAN_1: begin

			if (shift_count_1 < DIN_W-1) begin
				shift_count_1 <= shift_count_1 + 1;
				data_out <= data_in[shift_count_1];
			end
			else begin
				shift_count_1 <= 0;
				data_out <= data_in[shift_count_1];
			end

			if (shift_count_1 == 15) DONE <= 1;
			else DONE <= 0;
		end
	endcase
end

always @ (posedge clk) begin
	if (rst) PS <= IDLE;
	else PS <= NS;
end
