blocks:
always@(posedge clk)
	case(present_state)
		`menu1:  	 begin
						 menu_in <= 2'b00;
						 filter  <= 3'd0;
						 end
		`menu1_wait: begin
						 menu_in <= 2'b00;
						 filter  <= 3'd0;
						 end
		`menu2: 		 begin
						 menu_in <= 2'b01;
						 filter  <= 3'd0;
						 end
		`menu2_wait: begin
						 menu_in <= 2'b01;
						 filter  <= 3'd0;
						 end						 
		`menu3: 		 begin
						 menu_in <= 2'b10;
						 if(filter1)
							filter  <= 3'd1;
						 else if(filter2)
							filter  <= 3'd2;
						 else if(filter3)
							filter  <= 3'd3;
						 else if(filter4)
							filter <=  3'd4;
						 else	
							filter <=  3'd0;
						 end											
		`menu3_wait: begin
						 menu_in <= 2'b10;
						 filter  <= 3'd0;
						 end
		default:		 begin
						 menu_in <= 2'b00;
						 filter  <= 3'd0;
						 end
	endcase

always@(posedge clk)
if (reset)
	present_state <= `menu1;
else
	present_state <= next_state;

always@(*)
	case(present_state)
		`menu1: 		if(record)
							next_state = `menu1_wait;
						else
							next_state = `menu1;
		`menu1_wait:if(no_touch)
							next_state = `menu2;
						else
							next_state = `menu1_wait;
		`menu2: 		if(stop)
							next_state = `menu2_wait;
						else if (end_progress_bar)
							next_state = `menu3;
						else
							next_state = `menu2;
		`menu2_wait:if(no_touch)
							next_state = `menu3;
						else
							next_state = `menu2_wait;
		`menu3: 		if(record_new)
							next_state = `menu3_wait;
						else
							next_state = `menu3;
		`menu3_wait:if(no_touch)
							next_state = `menu1;
						else
							next_state = `menu3_wait;
		default: next_state = `menu1;
	endcase
