```verilog








module spi2adc (sysclk, start, channel, data_from_adc, data_valid, 
						sdata_to_adc, adc_cs, adc_sck, sdata_from_adc);

	input	sysclk;			
	input	start;			
	input	channel;			
	output [9:0] data_from_adc;	
	output data_valid;	
	output sdata_to_adc;	
	output adc_cs;			
	output adc_sck;		
	input sdata_from_adc;	



	wire	sysclk, start, sdata_from_adc;



	reg [9:0]	data_from_adc;
	reg			adc_cs;
	wire			sdata_to_adc, adc_sck, data_valid;


	parameter	SGL=1'b1;	
	parameter	MSBF=1'b1;	


	reg			clk_1MHz;	
	reg [4:0]	ctr;			
	reg			tick;			
	parameter	TIME_CONSTANT = 5'd24;  
	initial begin
		clk_1MHz = 0;			
		ctr = 5'b0;				
		tick = 1'b0;		
	end

	always @ (posedge sysclk)   
	  if (ctr==0) begin
		  ctr <= TIME_CONSTANT;
		  if (clk_1MHz==1'b0)
				tick <= 1'b1;
		  clk_1MHz <= ~clk_1MHz; 
		end
	  else  begin
		  ctr <= ctr - 1'b1;
		  tick <= 1'b0;
		end





	reg [1:0] 	sr_state;
	parameter	IDLE  = 2'b00,WAIT_CSB_FALL = 2'b01, WAIT_CSB_HIGH = 2'b10;
	reg			adc_start;

	initial begin
		sr_state = IDLE;
		adc_start = 1'b0;	
		end

	always @ (posedge sysclk)
		case (sr_state)
			IDLE:	if (start==1'b0) sr_state <= IDLE;
					else	begin
						sr_state <= WAIT_CSB_FALL;
						adc_start <= 1'b1;
						end  				
			WAIT_CSB_FALL: if (adc_cs==1'b1) sr_state <= WAIT_CSB_FALL;
					else sr_state <= WAIT_CSB_HIGH;

			WAIT_CSB_HIGH: if (adc_cs==1'b0) sr_state <= WAIT_CSB_HIGH;
					else begin
						sr_state <= IDLE;
						adc_start <= 1'b0;
						end	
			default: sr_state <= IDLE;
		endcase






	reg [4:0] 	state;
	reg  	adc_done, adc_din, shift_ena;

	initial	begin	
		state = 5'b0; adc_cs = 1'b1; adc_done = 1'b0; 
		adc_din = 1'b0; shift_ena <= 1'b0;
		end

	always @(posedge sysclk)  
		if (tick==1'b1)  begin


		adc_cs <= 1'b0;  adc_done <= 1'b0;  adc_din <= 1'b0; shift_ena <= 1'b0;
		state <= state + 1'b1;
		case (state)
			5'd0:	begin
						if (adc_start==1'b0) begin
							state <= 5'd0;			
							adc_cs <= 1'b1;		
							end
						else begin
							state <= 5'd1;			
							adc_din <= 1'b1;		
							end
					end
			5'd1:	adc_din <= SGL;				
			5'd2:	adc_din <= channel;			
			5'd3:	adc_din <= MSBF;				
			5'd4: shift_ena <= 1'b1;  			
			5'd15: begin
						shift_ena <= 1'b0;
						adc_done <= 1'b1;
					 end
			5'd16: begin  
						adc_cs <= 1'b1;		
						state <= 5'd0;  		
					 end
			default: 				
						shift_ena <= 1'b1;	
			endcase
		end	


	reg [9:0] shift_reg;
	initial	begin
			shift_reg = 10'b0;
			data_from_adc = 10'b0;
			end

	always @(negedge sysclk)
		if((adc_cs==1'b0)&&(shift_ena==1'b1)&&(tick==1'b1))		
				shift_reg <= {shift_reg[8:0],sdata_from_adc};


	always @(posedge sysclk)
		if(adc_done) 					
				data_from_adc = shift_reg;


		assign adc_sck = !clk_1MHz & !adc_cs;
		assign sdata_to_adc = adc_din;
		assign data_valid = adc_cs;
endmodule```

