blocks:
			assign dac_sck = !clk_1MHz&!dac_cs;
			assign dac_sdi = shift_reg[15];
endmodule

always @(posedge sysclk)
	if((dac_start==1'b1)&&(dac_cs==1'b1)&&(tick==1'b1))		
		shift_reg <= {cmd,data_in,2'b00};
	else if (tick==1'b1)													
		shift_reg <= {shift_reg[14:0],1'b0};

always @(posedge sysclk)  
	if (tick==1'b1)
	case (state)
		5'd0:	if (dac_start == 1'b1)    
					state <= state + 1'b1; 
				else 
					state <= 5'b0; 
		5'd17: 	state <= 5'd0;  
		default: state <= state + 1'b1;	
	endcase

initial	begin	
	state = 5'b0; dac_ld = 1'b0; dac_cs = 1'b1; 
	end

always @ (*)
	case (sr_state)
		IDLE: dac_start = 1'b0;
		WAIT_CSB_FALL: dac_start = 1'b1;
		WAIT_CSB_HIGH: dac_start = 1'b0;
		default: dac_start = 1'b0;
	endcase

always @ (posedge sysclk)  
	case (sr_state)
		IDLE:	if (load==1'b1) sr_state <= WAIT_CSB_FALL;
		WAIT_CSB_FALL: if (dac_cs==1'b0) sr_state <= WAIT_CSB_HIGH;
		WAIT_CSB_HIGH: if (dac_cs==1'b1) sr_state <= IDLE;
		default: sr_state <= IDLE;
	endcase

initial begin
	sr_state = IDLE;
	dac_start = 1'b0;	
	end

always @ (posedge sysclk)    
  if (ctr==0) begin
	  ctr <= TC;
	  if (clk_1MHz==1'b0) 
			tick <= 1'b1;
	  clk_1MHz <= ~clk_1MHz; 
	end
  else begin
	  ctr <= ctr - 1'b1;
	  tick <= 1'b0;
	end

initial begin
	clk_1MHz = 0;			
	ctr = 5'b0;				
	tick = 1'b0;
end
