always @(posedge clk) begin
    if (reset) begin
        lut_index <= 4'd0;
        i2c_start <= 1'b0;
        control_state <= 2'b00;
    end else begin
        case (control_state)
            2'b00: begin
                i2c_start <= 1'b1;
                i2c_data <= {8'h34, lut_data};
                control_state <= 2'b01;
            end
            2'b01: begin
                i2c_start <= 1'b0;
                control_state <= 2'b10;
            end
            2'b10: if (i2c_done) begin
                if (i2c_ack) begin
                    if (lut_index == LAST_INDEX)
                        control_state <= 2'b11;
                    else begin
                        lut_index <= lut_index + 1'b1;
                        control_state <= 2'b00;
                    end
                end else
                    control_state <= 2'b00;
            end
        endcase
    end
end

always @(*) begin
    case (lut_index)
        4'h0: lut_data <= 16'h0c10; 
        4'h1: lut_data <= 16'h0017; 
        4'h2: lut_data <= 16'h0217; 
        4'h3: lut_data <= 16'h0479; 
        4'h4: lut_data <= 16'h0679; 
        4'h5: lut_data <= 16'h08d4; 
        4'h6: lut_data <= 16'h0a04; 
        4'h7: lut_data <= 16'h0e01; 
        4'h8: lut_data <= 16'h1020; 
        4'h9: lut_data <= 16'h0c00; 
        4'ha: lut_data <= 16'h1201; 
        default: lut_data <= 16'h0000;
    endcase
end
