blocks:
always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
      begin
        d1_data_in <= 0;
        d2_data_in <= 0;
      end
    else if (clk_en)
      begin
        d1_data_in <= data_in;
        d2_data_in <= d1_data_in;
      end
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        edge_capture <= 0;
    else if (clk_en)
        if (edge_capture_wr_strobe)
            edge_capture <= 0;
        else if (edge_detect)
            edge_capture <= -1;
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        irq_mask <= 0;
    else if (chipselect && ~write_n && (address == 2))
        irq_mask <= writedata;
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        readdata <= 0;
    else if (clk_en)
        readdata <= {32'b0 | read_mux_out};
  end
