always @(posedge Sclk)
begin
	if (OutReady)
	begin
		writeL[i] = OutputL_beh ^ OutputL_rtl;
		writeR[i] = OutputR_beh ^ OutputR_rtl;
		OutputL = OutputL_beh ^ OutputL_rtl;
		OutputR = OutputR_beh ^ OutputR_rtl;
		i = i - 1;
		if (i < 0)
		begin
			$fmonitor(fp, "%H\t %H\n", writeL, writeR);
			i = 39;
			if (!(writeL == 40'd0 && writeR == 40'd0))
				mismatches = mismatches + 1'b1;
		end
	end
end

always @(posedge Dclk)
begin
	if ((((j == 9458) || (j == 13058)) && reset_flag == 1'b0))
	begin
		Reset_n = 1'b0;
		reset_flag = 1'b1;




	end
	else if (InReady || reset_flag)
	begin
		if (j < 15056)
		begin
			Reset_n = 1'b1;
			if (k == 15)
				Frame = 1'b1;
			else
				Frame = 1'b0;
			if (k >= 0)
			begin
				InputL = data[j][k];
				InputR = data[j+1][k];

				k = k - 1;
			end
			if (k == -1)
			begin
				k = 15;
				j = j + 2;
				if (reset_flag)
					reset_flag = 1'b0;
			end
		end
	end
end

	initial begin
		Dclk = 1;
		Sclk = 1;
		Frame = 0;
		InputL = 0;
		InputR = 0;
		Reset_n = 1;
		mismatches = 0;
		$readmemh ("data1.in", data);
		$display ("Read data: ");
    	Start = 1'b1;
		#2; Start = 1'b0;
	end
