initial
  begin
    $write("\n");
    $write("This reference design requires a vendor simulation model.\n");
    $write("To simulate accesses to SDRAM, you must:\n");
    $write("	 - Download the vendor model\n");
    $write("	 - Install the model in the system_sim directory\n");
    $write("	 - `include the vendor model in the the top-level system file,\n");
    $write("	 - Instantiate sdram simulation models and wire them to testbench signals\n");
    $write("	 - Be aware that you may have to disable some timing checks in the vendor model\n");
    $write("		   (because this simulation is zero-delay based)\n");
    $write("\n");
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        za_valid <= 0;
    else if (1)
        za_valid <= rd_valid[2];
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        za_data <= 0;
    else 
      za_data <= zs_dq;
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        rd_valid <= {3{1'b0}};
    else 
      rd_valid <= (rd_valid << 1) | { {2{1'b0}}, rd_strobe };
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
      begin
        m_state <= 9'b000000001;
        m_next <= 9'b000000001;
        m_cmd <= 4'b1111;
        m_bank <= 2'b00;
        m_addr <= 13'b0000000000000;
        m_data <= 32'b00000000000000000000000000000000;
        m_dqm <= 4'b0000;
        m_count <= 3'b000;
        ack_refresh_request <= 1'b0;
        f_pop <= 1'b0;
        oe <= 1'b0;
      end
    else 
      begin
        f_pop <= 1'b0;
        oe <= 1'b0;
        case (m_state) 

            9'b000000001: begin

                if (init_done)
                  begin

                    if (refresh_request)
                        m_cmd <= {{1{1'b0}},3'h7};
                    else 
                      m_cmd <= 4'b1111;
                    ack_refresh_request <= 1'b0;

                    if (refresh_request)
                      begin
                        m_state <= 9'b001000000;
                        m_next <= 9'b010000000;
                        m_count <= 0;
                        active_cs_n <= 1'b1;
                      end
                    else if (!f_empty)
                      begin
                        f_pop <= 1'b1;
                        active_cs_n <= f_cs_n;
                        active_rnw <= f_rnw;
                        active_addr <= f_addr;
                        active_data <= f_data;
                        active_dqm <= f_dqm;
                        m_state <= 9'b000000010;
                      end
                  end
                else 
                  begin
                    m_addr <= i_addr;
                    m_state <= 9'b000000001;
                    m_next <= 9'b000000001;
                    m_cmd <= i_cmd;
                  end
            end 

            9'b000000010: begin
                m_state <= 9'b000000100;
                m_cmd <= {csn_decode,3'h3};
                m_bank <= active_bank;
                m_addr <= active_addr[23 : 11];
                m_data <= active_data;
                m_dqm <= active_dqm;
                m_count <= 1;
                m_next <= active_rnw ? 9'b000001000 : 9'b000010000;
            end 

            9'b000000100: begin

                if (m_next == 9'b010000000)
                    m_cmd <= {{1{1'b0}},3'h7};
                else 
                  m_cmd <= {csn_decode,3'h7};

                if (m_count > 1)
                    m_count <= m_count - 1'b1;
                else 
                  m_state <= m_next;
            end 

            9'b000001000: begin
                m_cmd <= {csn_decode,3'h5};
                m_bank <= f_select ? f_bank : active_bank;
                m_dqm <= f_select ? f_dqm  : active_dqm;
                m_addr <= cas_addr;

                if (pending)
                  begin

                    if (refresh_request)
                      begin
                        m_state <= 9'b000000100;
                        m_next <= 9'b000000001;
                        m_count <= 2;
                      end
                    else 
                      begin
                        f_pop <= 1'b1;
                        active_cs_n <= f_cs_n;
                        active_rnw <= f_rnw;
                        active_addr <= f_addr;
                        active_data <= f_data;
                        active_dqm <= f_dqm;
                      end
                  end
                else 
                  begin

                    if (~pending & f_pop)
                        m_cmd <= {csn_decode,3'h7};
                    m_state <= 9'b100000000;
                  end
            end 

            9'b000010000: begin
                m_cmd <= {csn_decode,3'h4};
                oe <= 1'b1;
                m_data <= f_select ? f_data : active_data;
                m_dqm <= f_select ? f_dqm  : active_dqm;
                m_bank <= f_select ? f_bank : active_bank;
                m_addr <= cas_addr;

                if (pending)
                  begin

                    if (refresh_request)
                      begin
                        m_state <= 9'b000000100;
                        m_next <= 9'b000000001;
                        m_count <= 1;
                      end
                    else 
                      begin
                        f_pop <= 1'b1;
                        active_cs_n <= f_cs_n;
                        active_rnw <= f_rnw;
                        active_addr <= f_addr;
                        active_data <= f_data;
                        active_dqm <= f_dqm;
                      end
                  end
                else 
                  begin

                    if (~pending & f_pop)
                      begin
                        m_cmd <= {csn_decode,3'h7};
                        oe <= 1'b0;
                      end
                    m_state <= 9'b100000000;
                  end
            end 

            9'b000100000: begin
                m_cmd <= {csn_decode,3'h7};

                if (m_count > 1)
                    m_count <= m_count - 1'b1;
                else 
                  begin
                    m_state <= 9'b001000000;
                    m_count <= 0;
                  end
            end 

            9'b001000000: begin
                m_state <= 9'b000000100;
                m_addr <= {13{1'b1}};

                if (refresh_request)
                    m_cmd <= {{1{1'b0}},3'h2};
                else 
                  m_cmd <= {csn_decode,3'h2};
            end 

            9'b010000000: begin
                ack_refresh_request <= 1'b1;
                m_state <= 9'b000000100;
                m_cmd <= {{1{1'b0}},3'h1};
                m_count <= 3;
                m_next <= 9'b000000001;
            end 

            9'b100000000: begin
                m_cmd <= {csn_decode,3'h7};

                if (refresh_request)
                  begin
                    m_state <= 9'b000000100;
                    m_next <= 9'b000000001;
                    m_count <= 1;
                  end
                else 
                if (!f_empty)

                    if (csn_match && rnw_match && bank_match && row_match)
                      begin
                        m_state <= f_rnw ? 9'b000001000 : 9'b000010000;
                        f_pop <= 1'b1;
                        active_cs_n <= f_cs_n;
                        active_rnw <= f_rnw;
                        active_addr <= f_addr;
                        active_data <= f_data;
                        active_dqm <= f_dqm;
                      end
                    else 
                      begin
                        m_state <= 9'b000100000;
                        m_next <= 9'b000000001;
                        m_count <= 1;
                      end
            end 



            default: begin
                m_state <= m_state;
                m_cmd <= 4'b1111;
                f_pop <= 1'b0;
                oe <= 1'b0;
            end 


        endcase 
      end
  end

assign active_bank = {active_addr[24],active_addr[10]};
assign csn_match = active_cs_n == f_cs_n;
assign rnw_match = active_rnw == f_rnw;
assign bank_match = active_bank == f_bank;
assign row_match = {active_addr[23 : 11]} == {f_addr[23 : 11]};
assign pending = csn_match && rnw_match && bank_match && row_match && !f_empty;
assign cas_addr = f_select ? { {3{1'b0}},f_addr[9 : 0] } : { {3{1'b0}},active_addr[9 : 0] };

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
      begin
        i_state <= 3'b000;
        i_next <= 3'b000;
        i_cmd <= 4'b1111;
        i_addr <= {13{1'b1}};
        i_count <= {3{1'b0}};
      end
    else 
      begin
        i_addr <= {13{1'b1}};
        case (i_state) 

            3'b000: begin
                i_cmd <= 4'b1111;
                i_refs <= 3'b0;

                if (refresh_counter == 0)
                    i_state <= 3'b001;
            end 

            3'b001: begin
                i_state <= 3'b011;
                i_cmd <= {{1{1'b0}},3'h2};
                i_count <= 0;
                i_next <= 3'b010;
            end 

            3'b010: begin
                i_cmd <= {{1{1'b0}},3'h1};
                i_refs <= i_refs + 1'b1;
                i_state <= 3'b011;
                i_count <= 3;

                if (i_refs == 3'h1)
                    i_next <= 3'b111;
                else 
                  i_next <= 3'b010;
            end 

            3'b011: begin
                i_cmd <= {{1{1'b0}},3'h7};

                if (i_count > 1)
                    i_count <= i_count - 1'b1;
                else 
                  i_state <= i_next;
            end 

            3'b101: begin
                i_state <= 3'b101;
            end 

            3'b111: begin
                i_state <= 3'b011;
                i_cmd <= {{1{1'b0}},3'h0};
                i_addr <= {{3{1'b0}},1'b0,2'b00,3'h3,4'h0};
                i_count <= 4;
                i_next <= 3'b101;
            end 

            default: begin
                i_state <= 3'b000;
            end 

        endcase 
      end
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        init_done <= 0;
    else if (1)
        init_done <= init_done | (i_state == 3'b101);
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        za_cannotrefresh <= 0;
    else if (1)
        za_cannotrefresh <= (refresh_counter == 0) & refresh_request;
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        refresh_request <= 0;
    else if (1)
        refresh_request <= ((refresh_counter == 0) | refresh_request) & ~ack_refresh_request & init_done;
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        refresh_counter <= 10000;
    else if (refresh_counter == 0)
        refresh_counter <= 390;
    else 
      refresh_counter <= refresh_counter - 1'b1;
  end

assign clk_en = 1;

assign {zs_cs_n, zs_ras_n, zs_cas_n, zs_we_n} = m_cmd;
assign zs_addr = m_addr;
assign zs_cke = clk_en;
assign zs_dq = oe?m_data:{32{1'bz}};
assign zs_dqm = m_dqm;
assign zs_ba = m_bank;

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
      begin
        wr_address <= 0;
        rd_address <= 0;
        entries <= 0;
      end
    else 
      case (rdwr) 

          2'd1: begin

              if (!full)
                begin
                  entries <= entries + 1;
                  wr_address <= (wr_address == 1) ? 0 : (wr_address + 1);
                end
          end 

          2'd2: begin

              if (!empty)
                begin
                  entries <= entries - 1;
                  rd_address <= (rd_address == 1) ? 0 : (rd_address + 1);
                end
          end 

          2'd3: begin
              wr_address <= (wr_address == 1) ? 0 : (wr_address + 1);
              rd_address <= (rd_address == 1) ? 0 : (rd_address + 1);
          end 

          default: begin
          end 

      endcase 
  end
