blocks:
always@(negedge clock) begin
	if(bloque!=0 & bloque!=9) begin
		mosi<=gain[bloque-1];
	end
	else mosi<=0;
end

always@(posedge clock) begin
if(clockenable) begin
	if(bloque==0) begin
		bloque<=1;
		ampcs<=0;
	end
	else if (bloque==9) begin
		bloque<=0;
		estadosck<=0;
		ampcs<=1;
	end
	else begin
		bloque<=bloque+1;
		estadosck<=1;
	end
end
end

initial begin
	bloque<=0;
	ampcs<=1;
	ampshdn<=0;
	estadosck<=0;
	mosi<=0;
end
