always @(posedge ck) begin

    if (en) begin
        sd <= shift[15];

        if (frame == 0) begin
            shift <= left;
        end else if (frame == midpoint) begin
            shift <= right;
        end else begin
            shift <= shift << 1;
        end

    end

end

generate
    if (CLOCKS==64) begin
        assign MASK = 6'b111111;
        assign midpoint = 32;
    end
    if (CLOCKS==32) begin
        assign MASK = 6'b011111;
        assign midpoint = 16;
    end
endgenerate
