blocks:
initial begin
    $display("start");

    wait(!wb_rst);
    @(posedge wb_clk);
    @(posedge wb_clk);
    @(posedge wb_clk);
    @(posedge wb_clk);
    @(posedge wb_clk);

    for (i = 0; i < 16; i = i + 1) begin
        write(ADDR+(i*4), i + (i << 8) + (i << 16));
        wait(ack);
        wait(!ack);
        @(posedge wb_clk);
    end

    $display("done");

end

always @(posedge wb_clk) begin
    if (ack) begin
        wb_dbus_cyc <= 0;
        wb_dbus_adr <= 32'hZ;
        wb_dbus_dat <= 32'hZ;
        wb_dbus_we <= 0;
    end
end

task write(input [31:0] addr, input [31:0] data);

    begin
        wb_dbus_cyc <= 1;
        wb_dbus_adr <= addr;
        wb_dbus_dat <= data;
        wb_dbus_we <= 1;
    end

endtask
