always @(posedge Sclk)
begin

	if (col==-1)
	begin
		col_out = col;
		OutReady=0;
		col=39;
	end
	if(col<=38 && col>-1)
	begin
		col_out=col;
		OutputL=OutputdataL[col];
		OutputR=OutputdataR[col];
		col=col-1;
		col_out=col;
		if(col==-1)
			col_out=-1;
	end
end

always @(posedge Frame)
begin
	if(col==39 && P2S_status==1)
	begin
		OutReady=1;
		OutputL=OutputdataL[col];
		OutputR=OutputdataR[col];
		col=col-1;
		col_out=col;
	end
end
