blocks:
always @(posedge Sclk)
begin
	if(OutReady==1'b1)
	begin
	if (l==40)
	begin
		if(Frame==1'b1)
			k=k+1;
	end
	if(k<7000 && k>=0)
	begin
		if(l>-1)
		begin
			l=l-1;
			data_outl[k][l]= OutputL;
			data_outr[k][l]= OutputR;	
			if(l==0)
			begin
				$fwrite(data_out,"   %h      %h\n",data_outl[k],data_outr[k]);

				l=40;
			end
		end
	end
	if(k==7000)
	begin
		$fclose(data_out);

	end
	end
end

always @(posedge Dclk)
begin
	if(Reset_n==1'b1)
	begin
	if(InReady	== 1)
	begin

		if(ib<15056)
		begin
			if(jb==15)
			begin
				Frame = 1'b1;
			end
			if(jb>-1)
			begin
				InputL = input_data[ib][jb]; 
				InputR = input_data[ib+1][jb];#1302;
				Frame = 1'b0;
				jb = jb-1;
			end
			if(jb==-1)
			begin
				ib=ib+2;
				jb=15;
			end
		end
	end
	end
end

initial 
begin
	Dclk=1'b0;
	Sclk=1'b0;
	Reset_n=1'b1;

	Start = 1;
	$readmemh("midin.in", input_data) ;
	data_out=$fopen("data_out.out","w");


	#1302; Start = 0;
	Frame = 0;
	ib = 0;
	jb = 15;
	k = -1;
	l = 40;

end

always @ (*)
begin
	if(ib==9456 && jb==5)
	begin
		Reset_n = 1'b0;
		#13020; Reset_n = 1'b1;
	end
	if(ib==13056 && jb==9)
	begin
		Reset_n = 1'b0;
		#13020; Reset_n = 1'b1;
	end
end
