always @(negedge Sclk)
begin
	if (OutReady)
	begin
		writeL[l] = OutputL_reg ;
		writeR[l] = OutputR_reg ;
		OutputL = OutputL_reg ;
		OutputR = OutputR_reg;
		l = l - 1;
		if (l < 0 && count < 6395 )
		begin
			if(count!=3798 && count!= 5397)
			$fwrite (mv, "   %h      %h\n", writeL, writeR);
			count=count+1;
			l = 39;

		end
	end
end

always @(posedge Dclk)
begin
	if ((((m == 9458) || (m == 13058)) && flag_reset == 1'b0))
	begin
		Reset_n = 1'b0;
		flag_reset = 1'b1;
	end
	else if (InReady || flag_reset)
	begin
		if (m < 15056)
		begin
			Reset_n = 1'b1;
			if (n == 15 )
				Frame = 1'b1;
			else
				Frame = 1'b0;
			if (n >= 0)
			begin
				InputL = data[m][n];
				InputR = data[m+1][n];
				n = n - 1;
			end
			if (n == -1)
			begin
				n = 15;
				m = m + 2;
				if (flag_reset)
					flag_reset = 1'b0;
			end
		end
	end
end

	initial begin
		Dclk = 1;
		Sclk = 1;
		Frame = 0;
		InputL = 0;
		InputR = 0;
		Reset_n = 1;

		$readmemh ("data2.in", data);
    	Start = 1'b1;
		#2; Start = 1'b0;

		if (count==6394)
		$finish;
	end
