always @ (posedge Sclk or posedge Start or posedge done_R )
begin
	if(Start)
	begin
		 RJ_enR = 1'b0;
		  H_enR = 1'b0;
	   X_wr_enR = 1'b0;
		rw_ptr_R = 4'h0;
		hw_ptr_R = 9'h000;
		xw_ptr_R = 8'h00;

	end

	else if (done_R)
	begin
		RJ_enR = 1'b0;
		 H_enR = 1'b0;
		 X_wr_enR = 1'b0;
	end

	else
	begin
		case (next_state)
			state0:
			begin   
				if(R_clr_stop_R == 1'b0)
				begin
					rw_ptr_R = rw_ptr_buff_R;
					RJ_enR = 1'b1;
				end
				if(X_clr_stop_R == 1'b0)
				begin
					xw_ptr_R = xw_ptr_buff_R[7:0];
					X_wr_enR = 1'b1;
				end
				hw_ptr_R = hw_ptr_buff_R;
				H_enR = 1'b1;	
			end

			state1:
			begin	
				rw_ptr_R      = 4'h0;
			end

			state2:
			begin
				if(word_ready)
				begin
					rw_ptr_R = rw_ptr_buff_R;

					RJ_wrR = 1'b1;
					RJ_enR = 1'b1;
				end
			end

			state3:
			begin	
				RJ_wrR 		= 1'b0;
				hw_ptr_R      = 9'h000;
			end

			state4:
			begin
				if(word_ready)
				begin
					hw_ptr_R = hw_ptr_buff_R;
					H_wrR = 1'b1;
					H_enR = 1'b1;
				end
			end

			state5:
			begin
				H_wrR 		= 1'b0;
				xw_ptr_R		= 8'h00;
			end

			state6:
			begin
				if(word_ready)
				begin
					 xw_ptr_R = xw_ptr_buff_L[7:0];
					X_wr_enR = 1'b1;
				end
			end

			state7:
			begin
				rw_ptr_R = 4'h0;
				hw_ptr_R = 9'h000;
				xw_ptr_R = 8'h00;
			end

			state8:
			begin
				if(word_ready)
				begin
					xw_ptr_R = 8'h00;
					X_wr_enR = 1'b1;
				end
			end

			default:
			begin
				if (Start == 1'b1)
				begin
					rw_ptr_R = 4'h0;
					hw_ptr_R = 9'h000;
					xw_ptr_R = 8'h00;
				end
			end
		endcase
	end
end

always @ (posedge Sclk or negedge Reset or posedge Start or posedge done_L )
begin
	if(Reset == 1'b0)
	begin
		state <= state7;
		InReady = 1'b0;
			 n = 8'h00;
	end
	else if(Start== 1'b1)
	begin
		 RJ_enL = 1'b0;
		  H_enL = 1'b0;
	   X_wr_enL = 1'b0;
		RJ_rst = 1'b1;
		 H_rst = 1'b1;
		 X_rst = 1'b1;
		rw_ptr_L = 4'h0;
		hw_ptr_L = 9'h000;
		xw_ptr_L = 8'h00;
	  SIPO_rst = 1'b1;
			 n = 8'h00; 
		state <= state0;
	end

	else if (done_L== 1'b1)
	begin
		RJ_enL = 1'b0;
		 H_enL = 1'b0;
		 X_wr_enL = 1'b0;
	end

	else
	begin
		state <= next_state;
		case (next_state)
			state0:
			begin   
				SIPO_rst = 1'b0;
				if(R_clr_stop_L == 1'b0)
				begin
					rw_ptr_L = rw_ptr_buff_L;
					RJ_enL = 1'b1;
				end
				if(X_clr_stop_L == 1'b0)
				begin
					xw_ptr_L = xw_ptr_buff_L[7:0];
					X_wr_enL = 1'b1;
				end
				hw_ptr_L = hw_ptr_buff_L;
				H_enL = 1'b1;			
				if(hw_ptr_L == 9'h1FF)
				begin
					state <= state1;
				end
			end

			state1:
			begin	
				rw_ptr_L      = 4'h0;
				InReady     = 1'b1;
				RJ_rst      = 1'b0;
			end

			state2:
			begin
				if(word_ready)
				begin
					rw_ptr_L = rw_ptr_buff_L;
					if(rw_ptr_L == 4'hF)
					begin
						state <= state3;
					end
					RJ_wrL = 1'b1;
					RJ_enL = 1'b1;
				end
			end

			state3:
			begin	
				RJ_wrL 		= 1'b0;
				hw_ptr_L    = 9'h000;
				InReady     = 1'b1;
				 H_rst      = 1'b0;
			end

			state4:
			begin
				if(word_ready)
				begin
					hw_ptr_L = hw_ptr_buff_L;
					if(hw_ptr_L == 9'h1FF)
					begin
						state <= state5;
					end
					H_wrL = 1'b1;
					H_enL = 1'b1;
				end
			end

			state5:
			begin
				H_wrL 		= 1'b0;
				xw_ptr_L	= 8'h00;
				InReady		= 1'b1;
				X_rst		= 1'b0;
			end

			state6:
			begin
				if(word_ready)
				begin
					 xw_ptr_L = xw_ptr_buff_L[7:0];
						  n = xw_ptr_L;
					X_wr_enL = 1'b1;
				end

				if(sleep)
				begin
					state <= state8;
				end
			end

			state7:
			begin
				InReady = 1'b0;
					 n = 8'h00;
			end

			state8:
			begin
				if(word_ready)
				begin
					xw_ptr_L = 8'h0;
					X_wr_enL = 1'b1;
					n = xw_ptr_L; 
				end
				if(sleep == 1'b0)
				begin
					state <= state6;
				end
			end

			default:
			begin
				if (Start == 1'b1)
				begin
					rw_ptr_L = 4'h0;
					hw_ptr_L = 9'h000;
					xw_ptr_L = 8'h00;
					state <= state0;
				end
			end
		endcase
	end
end

always @ (posedge Start or posedge done_R or negedge Reset )
begin
	if(Reset == 1'b0)
	begin
		xw_ptr_buff_R = 9'h000;
	end

	else if(Start)
	begin
		{R_clr_stop_R,rw_ptr_buff_R} = 5'h0;
		hw_ptr_buff_R = 9'h000;
		{X_clr_stop_R,xw_ptr_buff_R} = 9'h000;
	end
	else
	begin
		case (next_state)
		state0:
			begin
				if(R_clr_stop_R == 1'b0)
				begin
					{R_clr_stop_R,rw_ptr_buff_R} =  rw_ptr_R + 1'b1;
				end		
				if(X_clr_stop_R == 1'b0)
				begin
					{X_clr_stop_R,xw_ptr_buff_R[7:0]} =  xw_ptr_R + 1'b1;
				end

				hw_ptr_buff_R  =  hw_ptr_R + 1'b1;
			end

		state1:
			begin
				rw_ptr_buff_R = 4'h0;
				xw_ptr_buff_R = 9'h000;
				hw_ptr_buff_R = 9'h000;
			end

		state2:
			begin
				rw_ptr_buff_R = rw_ptr_R + 1'b1;
			end

		state3:
			begin
				rw_ptr_buff_R = 4'h0;
			end

		state4:
			begin
				hw_ptr_buff_R = hw_ptr_R + 1'b1;
			end

		state5:
			begin
				hw_ptr_buff_R = 9'h000;
			end

		state6:
			begin
				if(sleep== 1'b1)
				begin
					xw_ptr_buff_R = 9'h000;
				end
				else
				begin
					xw_ptr_buff_R = xw_ptr_R + 1'b1;
				end
			end

		state7:
			begin
				rw_ptr_buff_R = 4'h0;
				xw_ptr_buff_R = 9'h000;
				hw_ptr_buff_R = 9'h000;
			end

		state8:
			begin
				if(sleep == 1'b1)
				begin
					xw_ptr_buff_R = 9'h000;
				end
				else
				begin
					xw_ptr_buff_R = 9'h001;
				end
			end

		default:
		begin
			rw_ptr_buff_R = 4'h0;
			xw_ptr_buff_R = 9'h000;
			hw_ptr_buff_R = 9'h000;
			R_clr_stop_R = 1'b0;
			X_clr_stop_R = 1'b0;
		end
		endcase
	end
end

always @ (posedge Start or posedge done_L or negedge Reset )
begin
	if(Reset == 1'b0)
	begin
		xw_ptr_buff_L = 9'h000;
		x_flagbit = 1'b0;
	end

	else if(Start== 1'b1)
	begin
		{R_clr_stop_L,rw_ptr_buff_L} = 5'h00;
		hw_ptr_buff_L = 9'h000;
		{X_clr_stop_L,xw_ptr_buff_L} = 9'h000;
		x_flagbit = 1'b0;
	end
	else
	begin
		case (next_state)
		state0:
			begin
				if(R_clr_stop_L == 1'b0)
				begin
					{R_clr_stop_L,rw_ptr_buff_L} =  rw_ptr_L + 1'b1;
				end		
				if(X_clr_stop_L == 1'b0)
				begin
					{X_clr_stop_L,xw_ptr_buff_L[7:0]} =  xw_ptr_L + 1'b1;
				end

				hw_ptr_buff_L  =  hw_ptr_L + 1'b1;
			end

		state1:
			begin
				rw_ptr_buff_L = 4'h0;
				xw_ptr_buff_L = 9'h000;
				hw_ptr_buff_L = 9'h000;
			end

		state2:
			begin
				rw_ptr_buff_L = rw_ptr_L + 1'b1;
			end

		state3:
			begin
				rw_ptr_buff_L = 4'h0;
			end

		state4:
			begin
				hw_ptr_buff_L = hw_ptr_L + 1'b1;
			end

		state5:
			begin
				hw_ptr_buff_L = 9'h000;
			end

		state6:
			begin
				if(sleep== 1'b1)
				begin
					xw_ptr_buff_L = 9'h0;
					x_flagbit = 1'b0;
				end
				else
				begin
					xw_ptr_buff_L = xw_ptr_L + 1'b1;
					if(xw_ptr_buff_L == 9'h100)
					begin
						x_flagbit = 1'b1;
					end
				end
			end

		state7:
			begin
				rw_ptr_buff_L = 4'h0;
				xw_ptr_buff_L = 9'h000;
				hw_ptr_buff_L = 9'h000;
			end

		state8:
			begin
				if(sleep == 1'b1)
				begin
					xw_ptr_buff_L = 9'h0;
				end
				else
				begin
					xw_ptr_buff_L = 9'h001;
				end
			end

		default:
		begin
			rw_ptr_buff_L = 4'h0;
			xw_ptr_buff_L = 9'h000;
			hw_ptr_buff_L = 9'h000;
			 R_clr_stop_L = 1'b0;
			 X_clr_stop_L = 1'b0;
		end
		endcase
	end
end

always @ (state or Frame or word_ready or Reset or Start or prev_OutReady or word_sent or sleep)
begin
	if(Reset == 1'b0)
	begin
		next_state <= state7;
		Frame_edg <= 1'b0;
		OutReady = 1'b0;
		ALU_en = 1'b0;
		PISO_en = 1'b0;
	end
	else if (Start== 1'b1)
	begin
		ALU_en = 1'b0;
		PISO_en = 1'b0;
		OutReady = 1'b0;

	end
	else
	begin
		case ( state )
			state0:
			begin
				if (R_clr_stop_L && R_clr_stop_R)
				begin
					next_state <= state1;
				end
				else
				begin
					next_state <= state0;
				end
			end

			state1:
			begin
				if (Frame)
				begin
					next_state <= state2;
					Frame_edg  <= 1'b1;
				end
				else
				begin
					next_state <= state1;
				end
			end

			state2:
			begin
				if (Frame)
				begin
					Frame_edg  <= 1'b1;
				end
				else if (word_ready)
				begin
					Frame_edg  <= 1'b0;
				end
				else
				begin
					next_state <= state2;
				end
			end

			state3:
			begin
				if (Frame)
				begin
					next_state <= state4;
					Frame_edg  <= 1'b1;
				end
				else
				begin
					next_state <= state3;
				end
			end

			state4:
			begin
				if (Frame)
				begin
					Frame_edg  <= 1'b1;
				end
				else if (word_ready)
				begin
					Frame_edg  <= 1'b0;
				end
				else
				begin
					next_state <= state4;
				end

			end

			state5:
			begin
				if (Frame)
				begin
					next_state <= state6;
					Frame_edg  <= 1'b1;
				end
				else
				begin
					next_state <= state5;
				end
			end

			state6:
			begin
				if (Frame)
				begin
					Frame_edg  <= 1'b1;
					if (prev_OutReady)
					begin
						PISO_en = 1'b1;
						OutReady = 1'b1;
					end
					if( (|{x_flagbit,xw_ptr_buff_L}) )
					begin
						ALU_en = 1'b1;
					end
				end

				else if (word_ready)
				begin
					Frame_edg  <= 1'b0;
				end
				else if (prev_OutReady)
				begin
					ALU_en = 1'b0;
				end

				else if (word_sent)
				begin
					PISO_en = 1'b0;
					OutReady = 1'b0;
				end
				else
				begin
					next_state <= state6;
				end

			end

			state7:
			begin
				if(Reset)
				begin
					next_state <= state5;
				end
			end

			state8:
			begin
				if (Frame)
				begin
					Frame_edg  <= 1'b1;
					if (prev_OutReady)
					begin
						PISO_en = 1'b1;
						OutReady = 1'b1;
					end
					ALU_en = 1'b1;
				end

				else if (!sleep)
				begin
					ALU_en = 1'b0;
				end

				else if (word_ready)
				begin
					Frame_edg  <= 1'b0;
				end

				else if (word_sent)
				begin
					PISO_en = 1'b0;
					OutReady = 1'b0;
				end

				else
				begin
					next_state <= state8;
				end
			end

			default:
			begin
				Frame_edg <= 1'b0;
				OutReady = 1'b0;
				ALU_en = 1'b0;
				PISO_en = 1'b0;
			end
		endcase
	end
end

always @ (posedge Sclk or posedge Start)
begin
	if(Start)
	begin
		data = 40'h0000000000;
		data_out = 1'b0;
		ybt = 6'h00;
		word_sent = 1'b0;
	end
	else 
	begin
		if(en)
		begin
			data = data_in;
			data_out = data[ybt];
			ybt = ybt + 1'b1;
			if(ybt == 6'h28)
			begin
				word_sent = 1'b1;
				ybt = 6'h00;
			end
		end
		else
		begin
			word_sent = 1'b0;
		end
	end
end

module PISO (en, Sclk, Start, data_in, word_sent, data_out);

input en, Sclk, Start;
input [39:0] data_in;
output reg word_sent, data_out;
reg [5:0] ybt;
reg [39:0] data;

always @ (negedge Reset or posedge valid or posedge Start)
begin
	if (Reset == 1'b0)
	begin
		rr_ptr_buff = 4'h0;
		hr_ptr_buff = 9'h000;

	end
	else if(Start== 1'b1)
	begin
		rr_ptr_buff = 4'h0;
		hr_ptr_buff = 9'h000;

	end
	else
	begin
		if (r_valid)
		begin
			rr_ptr_buff = rr_ptr + 1'b1;
		end




		if (h_valid)
		begin
			hr_ptr_buff = hr_ptr + 1'b1;
		end

	end
end

always @ (posedge Sclk or negedge Reset or posedge Start or posedge valid)
begin
	if(Reset == 1'b0)
	begin
		RJ_en = 1'b0;
		rr_ptr = 4'h0;
		rj_data = 16'h0000;
		rj_read = 1'b0;

		H_en = 1'b0;
		hr_ptr = 9'h000;
		h_cntr = 8'h00;
		h_data = 16'h0000;
		h_read = 1'b0;
		calc = 1'b0;
		h_finish = 1'b0;
		last_h = 1'b0;

		X_rd_en = 1'b0;
		xr_ptr = 8'h00;
		x_read = 1'b0;
		x_data = 16'h0000;
		x_finish = 1'b0;
		last_x = 1'b0;

		U = 40'h0000000000;
		U_curr = 40'h0000000000;
		y = 40'h0000000000;
		neg = 2'h0;
		prev_OutReady = 1'b0;
	end
	else if(Start== 1'b1)
	begin
		RJ_en = 1'b0;
		rr_ptr = 4'h0;
		rj_data = 16'h0000;
		rj_read = 1'b0;

		H_en = 1'b0;
		hr_ptr = 9'h000;
		h_cntr = 8'h00;
		h_data = 16'h0000;
		h_read = 1'b0;
		calc = 1'b0;
		h_finish = 1'b0;
		last_h = 1'b0;

		X_rd_en = 1'b0;
		xr_ptr = 8'h00;
		x_read = 1'b0;
		x_data = 16'h0000;
		x_finish = 1'b0;
		last_x = 1'b0;

		U = 40'h0000000000;
		U_curr = 40'h0000000000;
		y = 40'h0000000000;
		neg = 2'h0;
		prev_OutReady = 1'b0;
	end
	else if(valid== 1'b1)
	begin
	    RJ_en = 1'b0;
		H_en = 1'b0;
		X_rd_en = 1'b0; 
	end

	else
	begin
		if(en== 1'b1)
		begin
			prev_OutReady = 1'b0;
			if(sleep== 1'b0)
			begin
				if(x_read== 1'b1)
				begin
					if(neg[1]== 1'b0)
					begin
						x_data = x_data_in;
					end
					else
					begin
						x_data = 16'h0000; 
					end
					x_read = 1'b0;

					if ( h_sign== 1'b0 )
					begin
						U = U + {{8{x_data[15]}},x_data,{16{1'b0}}};
						U_curr = U_curr + {{8{x_data[15]}},x_data,{16{1'b0}}};
					end

					else
					begin
						U = U - {{8{x_data[15]}},x_data,{16{1'b0}}};
						U_curr = U_curr - {{8{x_data[15]}},x_data,{16{1'b0}}};
					end
				end

				if(x_finish== 1'b1)
				begin


					U = {U[39],U[39:1]};
					U_curr = {40{1'b0}};
					if(last_x == 1'b1)
					begin
						last_x = 1'b0;
						y = U;


						prev_OutReady = 1'b1;
						calc = 1'b0;
					end
					x_finish = 1'b0;
				end

				if(h_finish== 1'b1)
				begin
					x_finish = 1'b1;
					if(last_h== 1'b1)
					begin
						last_x = 1'b1;
					end
					h_finish = 1'b0;
				end

				if(h_read== 1'b1)
				begin
					h_data = h_data_in;

					h_read = 1'b0;
					calc = 1'b1;
				end

				if(calc== 1'b1)
				begin
					{neg,xr_ptr} = {x_flagbit, n} - h_data[7:0];
					if( neg[1]== 1'b0 )
					begin
						X_rd_en = 1'b1;

					end
					h_sign = h_data[8];
					x_read = 1'b1;
				end

				if(rj_read== 1'b1)
				begin
					rj_data = rj_data_in;
					rj_read = 1'b0;
				end

				if(h_cntr != rj_data)
				begin
					hr_ptr = hr_ptr_buff;
					H_en = 1'b1;
					h_read = 1'b1;
					h_cntr = h_cntr + 1'b1;
				end

				if(((h_cntr == rj_data) || (rr_ptr_buff == 4'h0)) && (last_h == 1'b0))
				begin
					if(rr_ptr == 4'hf)
					begin
						last_h = 1'b1;
						h_finish = 1'b1;
						rr_ptr = rr_ptr_buff;
					end
					else
					begin
						rr_ptr = rr_ptr_buff;
						if(rr_ptr != 1'b0)  
						begin
							h_finish = 1'b1;
						end
						h_cntr = 8'h00;
						RJ_en = 1'b1;
						rj_read = 1'b1;
					end
				end
			end
		end

		else
		begin
			RJ_en = 1'b0;
			rr_ptr = 4'h0;
			rj_data = 16'h0000;
			rj_read = 1'b0;

			H_en = 1'b0;
			hr_ptr = 9'h000;
			h_cntr = 8'h00;
			h_data = 16'h0000;
			h_read = 1'b0;
			calc = 1'b0;
			h_finish = 1'b0;
			last_h = 1'b0;

			X_rd_en = 1'b0;
			x_read = 1'b0;
			x_data = 16'h0000;
			x_finish = 1'b0;
			last_x = 1'b0;

			U = 40'h0000000000;
			U_curr = 40'h0000000000;
			neg = 2'h0;
		end
	end
end

