always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        data_dir <= 0;
    else if (chipselect && ~write_n && (address == 1))
        data_dir <= writedata;
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        data_out <= 0;
    else if (chipselect && ~write_n && (address == 0))
        data_out <= writedata;
  end

always @(posedge clk or negedge reset_n)
  begin
    if (reset_n == 0)
        readdata <= 0;
    else if (clk_en)
        readdata <= {32'b0 | read_mux_out};
  end
