always @ (posedge clk or negedge reset_n)
begin
	if (~reset_n)
		write <= 1'b0;
	else if (~pre_data_ready & data_ready )
		write <= 1'b1;
	else if (write)
		write <=0; 
end

always @ (clk)
begin
	if (~reset_n)
		 s_readdata <=32'b0;
	else if (s_read && (s_address == `IR_RX_CS_REG))
		s_readdata <= use_dw;

	else if (read && (s_address == `IR_RX_DATA_REG ))
	   s_readdata <= readdata;

	else
		 s_readdata <=32'b0;


end

always @ (clk)
begin
	if (~reset_n)
		fifo_clear <= 1'b0;
	else if (s_write && (s_address == `IR_RX_CS_REG))
		fifo_clear <= s_writedata[0];
	else if (fifo_clear)
		fifo_clear <= 1'b0;
end

always @ (posedge clk or negedge reset_n)
begin
	if (~reset_n)
		irq <= 1'b0;
	else if (~pre_data_ready & data_ready )
		irq <= 1'b1;
	else if (s_write && (s_address == `IR_RX_CS_REG))
		irq <=~s_writedata[1]; 
end

always @ (posedge clk or negedge reset_n)
begin
	if (~reset_n)
		pre_data_ready <= 1'b0;
	else
		pre_data_ready <= data_ready;
end
