task reg_write(input [ADDR_WIDTH-1:0] addr, input [DATA_WIDTH-1:0] data);
begin
	@(posedge pclk_i);
	paddr_i = addr;
	pwdata_i = data;
	pwrite_i = 1;
	penable_i = 1;
	wait (pready_o == 1);
	@(posedge pclk_i);
	pwrite_i = 0;
	penable_i = 0;
	paddr_i = 0;
	pwdata_i = 0;
end
endtask

initial begin
	prst_i = 1;
	paddr_i = 0;
	pwdata_i = 0;
	pwrite_i = 0;
	penable_i = 0;
	miso = 1;
	repeat(2) @(posedge pclk_i);
	prst_i  = 0;

	for (i = 0; i < MAX_TXS; i=i+1) begin
		reg_write(i, 8'hD3+i);    
	end

	for (i = 0; i < MAX_TXS; i=i+1) begin
		reg_write(8'h10+i, 8'h46+i);   
	end

	reg_write(8'h20, {3'h2, 1'b1});   
	#300;
	reg_write(8'h20, {3'h3, 1'b1});   
	#300;
	reg_write(8'h20, {3'h0, 1'b1});    
	#1000;
	$finish;
end

initial begin
	sclk_ref_i = 0;
	forever #1 sclk_ref_i =  ~sclk_ref_i;
end

